mapper.select:
/*[AdminModelApi_SQL.xml].[getModelList] */ SELECT RNUM, idx, category_cd, category_nm, category_age, model_kor_name, model_eng_name, model_description, visible, height, size3, shoes, model_main_yn, model_first_name, model_second_name, model_kor_first_name, model_kor_second_name, create_time FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS RNUM, idx, category_cd, category_nm, category_age, model_kor_name, model_eng_name, model_description, visible, height, size3, shoes, model_main_yn, model_first_name, model_second_name, model_kor_first_name, model_kor_second_name, create_time FROM ( SELECT tm.idx, tm.category_cd, tc.category_nm, tm.category_age, tm.model_kor_name, tm.model_eng_name, tm.model_description, tm.visible, tm.height, tm.size3, tm.shoes, tm.model_main_yn, tm.model_first_name, tm.model_second_name, tm.model_kor_first_name, tm.model_kor_second_name, DATE_FORMAT(tm.create_time,'%Y-%m-%d') AS create_time FROM ( SELECT @ROWNUM := 0) temp, tsp_model tm LEFT OUTER JOIN tsp_cmm_code tc ON tm.category_cd = tc.category_cd WHERE tm.visible = 'Y' AND tc.cmm_type = 'model' AND tc.visible = 'Y' AND tc.category_cd = #{categoryCd}
AND (model_kor_name LIKE CONCAT ('%', #{searchKeyword},'%') OR model_eng_name LIKE CONCAT ('%', #{searchKeyword}, '%') OR model_description LIKE CONCAT ('%', #{searchKeyword},'%'))
AND (model_kor_name LIKE CONCAT ('%', #{searchKeyword},'%') OR model_eng_name LIKE CONCAT ('%', #{searchKeyword}, '%'))
AND model_description LIKE CONCAT ('%', #{searchKeyword},'%')
ORDER BY idx DESC ) AS A ) AS B WHERE RNUM <= #{startPage}*#{size} AND RNUM > (#{startPage}-1)*#{size}

mapper.update:
/*[AdminModelApi_SQL.xml].[updateModel] */ UPDATE tsp_model SET
model_kor_name = CONCAT(#{modelKorFirstName}, #{modelKorSecondName}),
model_eng_name = CONCAT(#{modelFirstName}, #{modelSecondName}),
model_description = #{modelDescription}, category_age = #{categoryAge}, height = #{height}, size3 = #{size3}, shoes = #{shoes}, model_main_yn = #{modelMainYn}, model_first_name = #{modelFirstName}, model_second_name = #{modelSecondName}, model_kor_first_name = #{modelKorFirstName}, model_kor_second_name = #{modelKorSecondName}, career = #{careerList}, updater = #{updater}, update_time = NOW() WHERE idx = #{idx} AND category_cd = #{categoryCd}

/*[AdminModelApi_SQL.xml].[deleteModelImage] */
UPDATE tsp_image
SET visible = 'N',
	reg_date = NOW()
WHERE idx = #{idx}
AND type_name = 'model'

/*[AdminModelApi_SQL.xml].[deleteModel] */
UPDATE tsp_model
SET visible = #{visible},
	update_time = NOW()
WHERE idx = #{idx}
