mapper.select:
/*[AdminModelApi_SQL.xml].[getModelListCnt] */
SELECT COUNT(*)
FROM tsp_model
WHERE visible = 'Y'
AND category_cd = #{categoryCd}

mapper.update:
/*[AdminModelApi_SQL.xml].[deleteModelImage] */
UPDATE tsp_image
SET visible = 'N',
	reg_date = NOW()
WHERE idx = #{idx}
AND type_name = 'model'

/*[AdminModelApi_SQL.xml].[deleteModel] */
UPDATE tsp_model
SET visible = #{visible},
	update_time = NOW()
WHERE idx = #{idx}
