/*[AdminPortFolioApi_SQL.xml].[getPortFolioList] */ SELECT RNUM, idx, category_cd, category_nm, title, description, hash_tag, video_url, create_time FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS RNUM, tp.idx, tp.category_cd, tp.category_nm, tp.title, tp.description, tp.hash_tag, tp.video_url, tp.create_time FROM ( SELECT @ROWNUM := 0) temp, tsp_portfolio tp LEFT OUTER JOIN tsp_cmm_code tcc ON tp.category_cd = tcc.category_cd WHERE tp.visible = 'Y' AND tcc.cmm_type = 'portfolio'
AND (tp.title LIKE CONCAT ('%', #{searchKeyword},'%') OR tp.description LIKE CONCAT ('%', #{searchKeyword},'%'))
AND tp.title LIKE CONCAT ('%', #{searchKeyword},'%')
AND tp.description LIKE CONCAT ('%', #{searchKeyword},'%')
ORDER BY tp.idx DESC ) AS A WHERE A.RNUM <= #{startPage}*#{size} AND A.RNUM > (#{startPage}-1)*#{size}
