mapper.select:
/*[AdminUserApi_SQL.xml].[getUserId] */
SELECT user_id as id, password
FROM app_user_info
WHERE user_id = #{id}

/*[AdminUserApi_SQL.xml].[adminLogin] */
SELECT PASSWORD
FROM app_user_info
WHERE USER_ID = #{userId}

/*[AdminUserApi_SQL.xml].[selectAdminSeq] */
SELECT USER_SEQ as userSeq,
       name
FROM app_user_info
WHERE user_token = #{userToken}

mapper.insert:
/*[AdminUserApi_SQL.xml].[insertUserToken] */
UPDATE app_user_info SET user_token = #{userToken}
WHERE USER_ID = #{userId}
