mapper.insert:
/* [CommonImage_SQL.xml].[addImageFile] */
INSERT INTO tsp_image
( type_idx
, type_name
, file_num
, file_name
, file_size
, file_mask
, file_path
, image_type
, reg_date)
VALUES ( #{typeIdx}
       , #{typeName}
	   , #{fileNum}
	   , #{fileName}
	   , #{fileSize}
	   , #{fileMask}
	   , #{filePath}
	   , #{imageType}
	   , now())

mapper.delete:
/* [CommonImage_SQL.xml].[deleteImageFile] */
UPDATE tsp_image SET visible = 'N'


        WHERE type_idx = #{idx}
        AND image_type = #{imageType}


		WHERE idx = #{idx}
