mapper.insert:
/* [CommonImage_SQL.xml].[addImageFile] */
INSERT INTO tsp_model_image
( model_idx
, file_num
, file_name
, file_size
, file_mask
, file_path
, reg_date)
VALUES ( #{modelIdx}
	   , #{fileNum}
	   , #{fileName}
	   , #{fileSize}
	   , #{fileMask}
	   , #{filePath}
	   , now())

mapper.update:
/* [AdminImageApi_SQL.xml].[updateImageFile] */
UPDATE app_image_file_info
SET
board_type_cd = #{boardTypeCd},
board_seq = #{boardSeq},
image_type_cd = #{imageTypeCd},
image_file_id = #{imageFileId},
image_file_seq = #{imageFileSeq},
sort_order = #{sortOrder},
visible = #{visible},
updater = #{updater},
update_time = NOW()

	AND image_file_info_seq = #{imageFileInfoSeq}
	AND board_type_cd = #{boardTypeCd}
	AND board_seq = #{boardSeq}
	AND image_type_cd = #{imageTypeCd}
	AND sort_order = #{sortOrder}
